function oncalcpolar(ix)
		{
			if( ix==0 )
			{
				car = document.calcform2.z_car.value;
				c   = document.calcform2.cz.selectedIndex;
			}
			else
			{
				car = document.calcform2.s_car.value
				c   = document.calcform2.cs.selectedIndex;
			}

			if( car=='' ) return;
			i = car.indexOf('j');
			if( i==-1 ) car+='+j0';
			if( i==0  ) car='0+'+car;
			if( i==1  ) car='0'+car;

			len = car.length;
			i1 = car.indexOf('+');
			i2 = car.indexOf('-');
			i3 = car.indexOf(' ');
			if( i2==0 ) sign1=-1; else sign1=1; 
			if( i1<1 ) i1=100;
			if( i2<1 ) i2=100;
			if( i3<1 ) i3=100; 
			i = Math.min(i1,i2,i3);
			if( i==i2 ) sign2=-1; else sign2=1;
			x = car.substring(0,i);

			x = parseFloat(x);

			i = car.indexOf('j');
			y = car.substring(i+1,len);
			y = parseFloat(y);

			y*= sign2;
			mag = Math.sqrt(x*x+y*y);
			if( x!=0 )
				ang = Math.atan(y/x)*180/Math.PI;
			else
				ang = (y>0 ? 90 : (y<0 ? -90 : 0));
			if( sign1==-1 && ang!=0 )
			{
				if( ang>0 ) 
					ang-=180;
				else
					ang+=180;
			}

			mag /= Math.pow(10,(c*3));

			mag = mag.toFixed(2);
			ang = ang.toFixed(0);

			if( ix==0 )
			{
				document.calcform2.z.value = mag;
				document.calcform2.zdeg.value = ang;
			}
			else
			{
				document.calcform2.s.value = mag;
				document.calcform2.sdeg.value = ang;
			}
		}

function OnCalc() {
			var iphase = document.calcform.phase.selectedIndex;
			var ivolt = document.calcform.volt_type.selectedIndex;
			var kw   = document.calcform.kw.value;
			var amp  = document.calcform.amp.value;
			var volt = document.calcform.volt.value;
			var hz = document.calcform.hz.value;
			var pf_cor = document.calcform.pfc.value;
			if( volt=='' ) volt=120;
			if( hz=='' ) hz=60;
			var kva = volt*amp/1000;
			if( iphase==1 )
			{
				if( ivolt==0 )
				{
			 		kva*=Math.sqrt(3);
			 		volt/=Math.sqrt(3);
			 	}
			 	else
			 		kva*=3;
			}
			pf = kw/kva;
			kvar = Math.sqrt(kva*kva-kw*kw);


			if( pf>1 ) {alert("Error: power factor > 1\n1000\u00D7"+kw+"kW > "+amp+"A\u00D7"+volt+"V"); return;}
			var kva_cor = kw/pf_cor;
			var kvar_cor = Math.sqrt(kva_cor*kva_cor-kw*kw);
			var kvar_c = kvar-kvar_cor;
			if( iphase==1 )	kvar_c/=3;
			c = 1000000000*kvar_c/(2*Math.PI*hz*volt*volt);
		 	pf = pf.toFixed(3);
		 	kva = kva.toFixed(6);
		 	kvar = kvar.toFixed(6);
		 	c = c.toFixed(6);
		 	document.calcform.pf.value = pf;
		 	document.calcform.kva.value = kva;
		 	document.calcform.kvar.value = kvar;
		 	document.calcform.c.value = c;
		}

