      var x = document.calcform.x.value;
      var y = parseFloat(x);
      y = roundresult(y/100);
      if( y<0 )
      {
         y = -y;
         sign = '-';
      }
      var d = digits_after_period(y);
      den = Math.round(Math.pow(10,d));
      num = Math.round(y*den);
      g = gcd(num,den);
      num2 = Math.round(num/g);
      den2 = Math.round(den/g);
      y = sign+num+'/'+den;
      if( g>1 )
         y+= ' = '+sign+num2+'/'+den2;
      if( num2>den2 )
      {
         y+= ' = '+sign+Math.floor(num2/den2);
         if( (num2%den2)!=0 )
            y+= ' '+(num2%den2)+'/'+den2;
      }


      document.getElementById("area").value = y;

      var obj = document.getElementById("frac");
      CircNumber(obj,{
         n: 0,
         num: num2,
         den: den2,
         radius: 20,
         fillColor: "rgb(80,192,0)",
         lineWidth: 1
      });
   }

