template.css:
.calculator-container {
    max-width: 600px;
    margin: 0 auto;
    padding: 20px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-family: Arial, sans-serif;
}

.input-section, .output-section {
    margin: 20px 0;
}

.control-section {
    text-align: center;
    margin: 20px 0;
}

.calculate-btn {
    background-color: #007bff;
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 4px;
    cursor: pointer;
    font-size: 16px;
}

.calculate-btn:hover {
    background-color: #0056b3;
}

.input-group {
    margin: 10px 0;
}

.input-group label {
    display: inline-block;
    width: 120px;
    font-weight: bold;
}

.input-group input {
    width: 200px;
    padding: 8px;
    border: 1px solid #ccc;
    border-radius: 4px;
}

.output-group {
    margin: 10px 0;
    padding: 10px;
    background-color: #f8f9fa;
    border-radius: 4px;
}

.output-label {
    font-weight: bold;
    color: #333;
}

.output-value {
    font-size: 18px;
    color: #007bff;
    margin-left: 10px;
}

